import React, { useState } from 'react';

import LoggedFilm from './LoggedFilm'
import Card from '../UI/Card'
import EntriesFilter from "../UI/EntriesFilter";
import './FilmLog.css'

const FilmLog = (props) => {
    const [filteredYear, setFilteredYear] = useState('2023');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear);
    }

    return (
        <Card className="films">
            <EntriesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
            {props.entries.map(n => 
            <LoggedFilm
                title={props.entries[0].title}
                rating={props.entries[0].rating}
                date={props.entries[0].date}
            />
            )}
            <LoggedFilm
                title={props.entries[0].title}
                rating={props.entries[0].rating}
                date={props.entries[0].date}
            />
            <LoggedFilm
                title={props.entries[1].title}
                rating={props.entries[1].rating}
                date={props.entries[1].date}
            />
            <LoggedFilm
                title={props.entries[2].title}
                rating={props.entries[2].rating}
                date={props.entries[2].date}
            />
            <LoggedFilm
                title={props.entries[3].title}
                rating={props.entries[3].rating}
                date={props.entries[3].date}
            />
        </Card>
    )
}

export default FilmLog;
