import React, { useState } from 'react';

import LoggedFilm from './LoggedFilm'
import Card from '../UI/Card'
import EntriesFilter from "../UI/EntriesFilter";
import './FilmLog.css'

const FilmLog = (props) => {
    const [filteredYear, setFilteredYear] = useState('2023');

    const filterChangeHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
    };

    const filteredEntries = props.entries.filter(entry => {
        return entry.date.getFullYear().toString() === filteredYear;
    });

    let filmLogContent = <p>No films logged for this year!</p>;

    if (filteredEntries.length > 0) {

    }
    return (
        <div>
            <Card className="films">
                <EntriesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
                {filteredEntries.length === 0 && <p>No films logged for this year!</p>}
                {filteredEntries.length > 0 &&
                    filteredEntries.map((entry) => (
                        <LoggedFilm
                            key={entry.id}
                            title={entry.title}
                            rating={entry.rating}
                            date={entry.date}
                        />
                    ))}
            </Card>
        </div>
    );
};

export default FilmLog;
